document.getElementById("formBasica").addEventListener("submit",(e=>{e.preventDefault(),tasaDeInteres=parseFloat(e.target.querySelector("[data-tasas]").value),interesDecimal=tasaDeInteres/100;const t=document.getElementById("cantidadBasica").value;let a=comprobarCantidad(t);if(a)return document.getElementById("errores").textContent=a,void mostrarElementos("divErrores","divBasico");let{rendimientoDiario:n}=calcularRendimientos(t,1),o=n.toFixed(2),r=n<1?"centavos":"pesos";document.getElementById("resultadosBasicos").textContent=`$${o} ${r}`,mostrarElementos("divBasico","divErrores")})),document.getElementById("formAvanzada").addEventListener("submit",(e=>{e.preventDefault(),tasaDeInteres=parseFloat(e.target.querySelector("[data-tasas]").value),interesDecimal=tasaDeInteres/100;const t=document.getElementById("cantidadAvanzada").value,a=document.getElementById("dias").value;let n=comprobarCantidad(t),o=comprobarDias(a);if(n||o)return document.getElementById("errores").textContent=n||o,void mostrarElementos("divErrores","divAvanzado");let{rendimientoTotal:r}=calcularRendimientos(t,a),d=r.toFixed(2),s=r<1?"centavos":"pesos";r>=1&&(d=Number(d).toLocaleString("en-US",{minimumFractionDigits:2}));let i=1==a?"día":"días";document.getElementById("diasUsuario").textContent=`${a} ${i}`,document.getElementById("resultadosAvanzados").textContent=`$${d} ${s}`,mostrarElementos("divAvanzado","divErrores")}));